package com.tirion.db.store.builder.stats.analyzer.minmax;

import com.tirion.common.bitmap.Bitmap;
import com.tirion.common.sequence.array.Array;
import com.tirion.db.store.builder.stats.model.minmax.DoubleMinMax;
import com.tirion.db.store.builder.stats.model.minmax.MinMax;

/**
 * This class was autogenerated. Do not edit manually.
 */
public final class DoubleMinMaxAnalyzer implements MinMaxAnalyzer {

	@Override
	public MinMax analyze(Array array) {
		double[] input = (double[]) array.getUnderlying();
		Bitmap nullBitmap = array.getNullBitmap();
		if(nullBitmap != null) {
			return analyzeNulls(input, nullBitmap);
		} else {
			return analyzeNoNulls(input);
		}
	}
	
	private MinMax analyzeNulls(double[] input, Bitmap nullBitmap) {
		double min = Double.MAX_VALUE;
		double max = Double.MIN_VALUE;
		for (int i = 0; i < input.length; i++) {
			if(nullBitmap.isSet(i)) {					
				continue;
			}
			final double value = input[i];			
			min = 				   Math.min(value, min);
			max = 				   Math.max(value, max);
		}
		return new DoubleMinMax(min, max);
	}
	
	private MinMax analyzeNoNulls(double[] input) {
		double min = Double.MAX_VALUE;
		double max = Double.MIN_VALUE;
		for (int i = 0; i < input.length; i++) {
			final double value = input[i];			
			min = 				   Math.min(value, min);
			max = 				   Math.max(value, max);
		}
		return new DoubleMinMax(min, max);
	}
}
