package com.tirion.db.store.builder.stats.model.range;

import java.text.DecimalFormat;
import com.tirion.common.type.Type;

import org.codehaus.jackson.annotate.JsonProperty;

/**
 * This class was autogenerated. Do not edit manually.
 */
public final class FloatRange implements Range {

	@JsonProperty
	private final float low;
	@JsonProperty
	private final float high;

	public FloatRange(float low, float high) {
		super();
		this.low = low;
		this.high = high;
	}
	
	/**
	 * Returns true if given range is subset of this range.
	 */
	public boolean contains(float lowValue, float highValue) {
		return low < lowValue && highValue < high;
	}
	
	/**
	 * Number of values between low and high.
	 * Exact value.
	 */
	public float getDiff() {
		return 			    (getHigh() - getLow() - 1);
	}

	public float getLow() {
		return low;
	}

	public float getHigh() {
		return high;
	}
	
	@Override
	public Type getType() {
		return Type.FLOAT;
	}

	@Override
	public long sizeInBytes() {
		return 4 + 4;
	}

	@Override
	public String toString() {
				DecimalFormat format = new DecimalFormat("#.##");
		return format.format(getLow()) + ".." + format.format(getHigh());
			}
}

